import React from 'react'
import '../css/style.css'
import { Form, Badge, Button, Popover, PopoverHeader, PopoverBody } from 'reactstrap';

class Agregar extends React.Component {
    constructor() {
        super();
        this.state = {
            popoverOpen: false,
        };
        this.toggle = this.toggle.bind(this);
    };

    componentDidMount() {
    }


    toggle() {
        this.setState(prevState => ({
            popoverOpen: !prevState.popoverOpen
        }));
    };


    render() {
        return (
            <><div>

                <nav className="navbar navbar-dark bg-dark">
                    <button className="navbar-toggler" id="buttonAddCat" isOpen={this.state.popoverOpen} toggle={this.toggle} type="button" data-toggle="collapse" data-target="#navbarToggleExternalContent"
                        aria-controls="navbarToggleExternalContent" aria-expanded="true" aria-label="Toggle navigation">
                        <span className="button">Agregar categoría</span>
                    </button>
                    <button className="navbar-toggler buttonAddPro" id="buttonAddProd" type="button" data-toggle="collapse" data-target="#navbarToggleExternalContent"
                        aria-controls="navbarToggleExternalContent" aria-expanded="true" aria-label="Toggle navigation">
                        <span className="button">Agregar producto</span>
                    </button>

                    <Button className="navbar-toggler" id="Popover1" color="info">
                    <Badge className="navbar-toggler">Agregar Producto</Badge>
                        <Popover placement="bottom" isOpen={this.state.popoverOpen} target="Popover1" toggle={this.toggle}>
                            <PopoverHeader><strong>Agregar Producto</strong></PopoverHeader>
                            <PopoverBody>
                                <Form action={() => this.agregarAlCarro(this.props.props)} method="post" class="form-cat">
                                    <div className="form-group">
                                        Título: <input type="text" class="form-control" id="titulo">
                                        </input></div>
                                    <div className="form-group">
                                        Código: <input type="text" class="form-control" id="codigo">
                                        </input></div>
                                    <div className="form-group">
                                        Características: <input type="text" class="form-control" id="caracteristicas">
                                        </input></div>
                                    <div className="form-group">
                                        Precio(€): <input type="number" min="0.0" class="form-control" id="precio">
                                        </input></div>
                                    <div className="form-group">
                                        Stock: <input type="number" min="0" class="form-control" id="cantidad">
                                        </input></div>
                                    <div className="form-group">
                                        Imágen: <input type="text" class="form-control" id="imagen">
                                        </input></div>
                                    <button type="submit" id="btnSubmit" class="btn btn-primary">Submit</button>
                                </Form>
                                <br></br>
                            </PopoverBody>
                        </Popover>
                    </Button></nav>


            </div></>






        )
    }
}

export default Agregar
