import React from 'react'
import '../css/style.css'
import { Form, Badge, Button, Popover, PopoverHeader, PopoverBody } from 'reactstrap';
import { listaProductosTV } from '../json/items-tv.json';
import App from '../App.js';

class Agregar extends React.Component {

    datosrefTitulo = React.createRef();
    datosrefCodigo = React.createRef();
    datosrefCaracteristicas = React.createRef();
    datosrefPrecio = React.createRef();
    datosrefStock = React.createRef();
    datosrefImagen = React.createRef();

    constructor() {
        super();
        this.state = {
            popoverOpen: false,
            listaProductos: [],
            listaProductosTV: [],
        };
        this.toggle = this.toggle.bind(this);
    };

    componentDidMount() {
        this.setState({ listaProductosTV: listaProductosTV })
        this.agregarProducto = this.agregarProducto.bind(this);
    }


    toggle() {
        this.setState(prevState => ({
            popoverOpen: !prevState.popoverOpen
        }));
    };

    agregarProducto = (e) =>{
        e.preventDefault();
        listaProductosTV.push({
            "titulo": this.datosrefTitulo.current.value,
            "codigo": this.datosrefCodigo.current.value,
            "caracteristicas": this.datosrefCaracteristicas.current.value,
            "precio": this.datosrefPrecio.current.value,
            "stock": this.datosrefStock.current.value,
            "imagen": this.datosrefImagen.current.value
        });
        listaProductosTV.push({
            "titulo": this.datosrefTitulo.current.value,
            "codigo": this.datosrefCodigo.current.value,
            "caracteristicas": this.datosrefCaracteristicas.current.value,
            "precio": this.datosrefPrecio.current.value,
            "stock": this.datosrefStock.current.value,
            "imagen": this.datosrefImagen.current.value
        });
        console.log(listaProductosTV)
        App.setState({listaProductosTV: listaProductosTV})
    };

    render() {
        return (
            <div>
                <nav className="navbar navbar-dark bg-dark">
                    <Button className="navbar-toggler" id="Popover1" color="info">
                        <Badge className="navbar-toggler">Agregar Producto</Badge>
                        <Popover placement="bottom" isOpen={this.state.popoverOpen} target="Popover1" toggle={this.toggle}>
                            <PopoverHeader><strong>Agregar Producto</strong></PopoverHeader>
                            <PopoverBody>
                                <Form onSubmit={this.agregarProducto} className="form">
                                    <div className="form-group">
                                        Título: <input type="text" className="form-control" ref={this.datosrefTitulo} id="titulo">
                                        </input></div>
                                    <div className="form-group">
                                        Código: <input type="text" className="form-control" ref={this.datosrefCodigo} id="codigo">
                                        </input></div>
                                    <div className="form-group">
                                        Características: <input type="text" className="form-control" ref={this.datosrefCaracteristicas} id="caracteristicas">
                                        </input></div>
                                    <div className="form-group">
                                        Precio(€): <input type="number" min="0.0" className="form-control" ref={this.datosrefPrecio} id="precio">
                                        </input></div>
                                    <div className="form-group">
                                        Stock: <input type="number" min="0" className="form-control" ref={this.datosrefStock} id="cantidad">
                                        </input></div>
                                    <div className="form-group">
                                        Imágen: <input type="text" className="form-control" ref={this.datosrefImagen} id="imagen">
                                        </input></div>
                                    <button type="submit" className="btn btn-primary">Submit</button>
                                </Form>
                            </PopoverBody>
                        </Popover>
                    </Button>
                </nav>
            </div>
        )
    }
}

export default Agregar
