import React,{Component} from 'react';
import {Card, Col, CardImg, CardBody, CardTitle, CardSubtitle, CardText} from 'reactstrap';
import '../js/accordion.js'
import '../css/producto.css';
import '../css/style.css';
import FichaProducto from './FichaProducto';
import {listaCarro} from '../json/listaCarro.json';
import {listaProductos} from '../json/listaProductos.json';

//function Producto(props) {
class Producto extends Component {
    constructor (props) {
        super();
        this.state = {
            stocksprod:null,
            modal:false,
            listaCarro:[],
            listaProductos:[],
            cantidadproductoreducido:null,
            stocks:null
        };
    };
    componentDidMount() {
        this.setState({listaCarro:listaCarro,listaProductos:listaProductos,stocks:this.props.props.stock})
        this.setState({stocksprod:this.props.stock});
        this.agregarAlCarro = this.agregarAlCarro.bind(this);
    }

    cambiarStock=(cantidad,listaCarro)=>{
        // console.log(listaCarro)
        this.setState({stocksprod:cantidad})
        this.props.listaCarro(listaCarro)
    }

    agregarAlCarro (propsNew) {
        // console.log(propsNew)
        let valor = parseInt(this.state.stocks)-1;
        console.log(valor);
        listaCarro.push({
            "titulo":propsNew.titulo,
            "precio":propsNew.precio
        });

        this.setState(prevState => ({
            modal:!prevState.modal,
            stocks:parseInt(this.state.stocks)-1
        }));

        this.props.reducirCantidad(valor,listaCarro)
    };

    render(){
        return (
            <Col sm="6" className="col-12 col-md-6">
                <Card className="Card" body>  
                    <CardImg src={this.props.imagen} className="item-imagen"></CardImg>
                    <CardBody>
                        <CardTitle className="text-center"><b>{this.props.titulo}</b></CardTitle>
                        <CardSubtitle><b>Código:</b> {this.props.codigo}</CardSubtitle>
                        <CardText className="text-justify"><b>Descripción:</b> {this.props.descripcion}</CardText>
                        <CardSubtitle><b>Precio:</b> {this.props.precio} €</CardSubtitle>
                        <CardText>Stock: {this.state.stocksprod} unidades</CardText>
                        <FichaProducto props={this.props} className="Button" reducirCantidad={(e,listaCarro)=>this.cambiarStock(e,listaCarro)}></FichaProducto>
                        <hr></hr>
                        <button className="item-button btn btn-primary addToCart" onClick={()=>this.agregarAlCarro(this.props.props)} data-id="1">AÑADIR AL CARRITO</button>
                    </CardBody>
                </Card>
            </Col>
        );
    };
};

export default Producto;
