import React from 'react';
import { Row, Container } from 'reactstrap';
import './css/App.css';
import './css/style.css'
import Producto from './componentes/producto';
import Navegacion from './componentes/Navegacion';
import { listaProductos } from './json/listaProductos.json';
import { listaProductosTV } from './json/items-tv.json';

//console.log(listaProductos);
//function App() {
class App extends React.Component {
  //se realiza el constructor antes del render
  constructor() {
    //metodo super
    super();

    //estado del componente
    this.state = {
      listaProductos: [],
      listaProductosTV: [],
      listaCarro: []
    };
  };
  componentDidMount() {
    this.setState({ listaProductos: listaProductos })
    this.setState({ listaProductosTV: listaProductosTV })
  }
  listaCarroVer = listaCarro => {
    console.log(listaCarro)
    this.setState({ listaCarro: listaCarro })
  }
  render() {
/*    const arregloComponente = this.state.listaProductos.map(
      (listaProductos, i) => {
        //console.log(i);
        return (
          <Producto
            key={i}
            titulo={listaProducto.titulo}
            imagen={listaProductos.imagen}
            codigo={listaProductos.codigo}
            descripcion={listaProductos.descripcion}
            precio={listaProductos.precio}
            stock={listaProductos.stock}
            listaCarro={(e) => this.listaCarroVer(e)}
          />
        );*/
        const arregloComponente = this.state.listaProductosTV.map(
          (listaProductosTV, i) => {
            //console.log(i);
            return (
              <Producto
                key={i}
                titulo={listaProductosTV.titulo}
                imagen={listaProductosTV.imagen}
                codigo={listaProductosTV.codigo}
                descripcion={listaProductosTV.descripcion}
                precio={listaProductosTV.precio}
                stock={listaProductosTV.stock}
                listaCarro={(e) => this.listaCarroVer(e)}
              />
            );
      }
    );
    return (
      //      <Container>
      //        <Navegacion titulo="Carro de compras con ReactJS" listaCarro={this.state.listaCarro}/>
      //          <Row>
      //            {arregloComponente}
      //          </Row>
      //      </Container>
      <Container>
        <Navegacion titulo="Carro de compras con ReactJS" listaCarro={this.state.listaCarro} />
        <div class="block-store">
          <h1 class="text-center">Productos</h1>
          <hr></hr>
          <section class="store">
            <div class="container">
              <div class="items">
                <div id="container-main">
                  <div class="accordion-container">
                    <a href="#" class="accordion-titulo">TV, vídeo y home cinema<span
                      class="toggle-icon"></span></a>
                    <div class="accordion-content">
                      <div class="items1">
                        <Row>
                          {arregloComponente}
                        </Row>
                      </div>
                    </div>
                  </div>


                  <div class="accordion-container">
                    <a href="#" class="accordion-titulo">Telefonía<span
                      class="toggle-icon"></span></a>
                    <div class="accordion-content">
                      <div class="items2">
                        <Row>
                          {arregloComponente}
                        </Row>
                      </div>
                    </div>
                  </div>

                  <div class="accordion-container">
                    <a href="#" class="accordion-titulo">Informática<span
                      class="toggle-icon"></span></a>
                    <div class="accordion-content">
                      <div class="items3">
                        <Row>
                          {arregloComponente}
                        </Row>
                      </div>
                    </div>
                  </div>

                </div>
              </div>
            </div>
          </section>
        </div>
      </Container>
    );
  };
};

export default App;
