import React from 'react';
import {listaCarro} from '../json/listaCarro.json';
import {Table, Button, Badge, PopoverHeader, PopoverBody} from 'reactstrap';

class Carro extends React.Component{
    constructor(){
        super();
        this.state = {
            popoverOpen:false,
            listaCarro:[],
        };
    };

    componentDidMount() {
        console.log("Carro componente: ",this.props.listaCarro)
        this.setState({listaCarro:listaCarro})
    }


    calcularTotal(){
        let arregloPrecios = [];
        let totalPrecio = 0;
        listaCarro.map(
            (listaCarro,i)=> {
                arregloPrecios.push(parseInt(listaCarro.precio));
                totalPrecio = arregloPrecios.reduce((a,b)=>(a+b),0)*1000;
                return (totalPrecio);
            }
        );

        return (new Intl.NumberFormat("es-CL").format(totalPrecio))
    };

    render(){
        const productosVenta = this.state.listaCarro.map(
            (listaCarro,i)=> {
                return(
                    <tr key={i}>
                        <td>{i+=1}</td>
                        <td>{listaCarro.titulo}</td>
                        <td>{listaCarro.precio}</td>
                    </tr>
                )
            }
        );

        return(
            <div>
                <Button id="Popover1">
                    <span><i className="material-icons">shopping_cart</i></span>
                    <Badge className="secundary">{productosVenta.length}</Badge>
                        <PopoverHeader>Lista de Productos</PopoverHeader>
                        <PopoverBody>
                            <Table>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Producto</th>
                                        <th>Precio</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {productosVenta}
                                </tbody>
                                <tfoot>
                                    <tr>
                                        <td><b>Total:</b></td>
                                        <td></td>
                                        <td>{this.calcularTotal()} €</td>
                                    </tr>
                                </tfoot>  
                            </Table>
                        </PopoverBody>
                </Button>
            </div>
        );
    };
};

export default Carro;
