import React,{Component} from 'react';
import {Card, Col, CardImg, CardBody, CardTitle, CardSubtitle, CardText} from 'reactstrap';
import '../js/accordion.js'
//import '../css/producto.css';
import '../css/style.css';
import FichaProducto from './FichaProducto';

//function Producto(props) {
class Producto extends Component {
    constructor (props) {
        super();
        this.state = {
            stocksprod:null
        };
    };
    componentDidMount() {
        this.setState({stocksprod:this.props.stock})
    }

    cambiarStock=(cantidad,listaCarro)=>{
        // console.log(listaCarro)
        this.setState({stocksprod:cantidad})
        this.props.listaCarro(listaCarro)
    }

    render(){
        return (
         /*   <Col sm="6" className="card-deck">
                <Card className="Card" body outline color="primary">  
                    <CardImg src={this.props.imagen}></CardImg>
                    <CardBody>
                        <CardTitle className="text-center text-danger">{this.props.titulo}</CardTitle>
                        <CardSubtitle><b>Código:</b> {this.props.codigo}</CardSubtitle>
                        <CardText className="text-justify"><b>Descripción:</b> {this.props.descripcion}</CardText>
                        <CardSubtitle><b>Precio:</b> {this.props.precio} €</CardSubtitle>
                        <CardText>Stock: {this.state.stocksprod} unidades</CardText>
                        <FichaProducto props={this.props} className="Button" reducirCantidad={(e,listaCarro)=>this.cambiarStock(e,listaCarro)}></FichaProducto>
                    </CardBody>
                </Card>
            </Col>*/
            <div className="col-12 col-md-6">
            <div className="item shadow mb-4">
                <img className="item-image" style="border: 1px solid; color: black;"
                    src={this.props.imagen}></img>
                <div>
                    <h5 className="item-title">{this.props.titulo}</h5>
                    <p className="item-codigo">Código: {this.props.codigo}</p>
                </div>
                <div className="item-details">
                    <p className="description-p"><b>Características: </b>{this.props.descripcion}</p>
                    <p>Stock: <p className="item-stock" id="text1">{this.state.stocksprod} unidades</p></p>
                </div>
                <div className="item-details">
                    <h4 className="item-price">{this.props.precio} €</h4>
                    <FichaProducto props={this.props} className="Button" reducirCantidad={(e,listaCarro)=>this.cambiarStock(e,listaCarro)}></FichaProducto>
                    <button className="item-button btn btn-primary addToCart" data-id="1">AÑADIR AL
                        CARRITO</button>
                </div>
            </div>
        </div>
        );
    };
};

export default Producto;
